    private void BtnRegistrarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_BtnRegistrarMouseClicked
        // TODO add your handling code here:
        if(buscarInfo(TxtUsername.getText()))
            JOptionPane.showMessageDialog(this, "Ya existe un registro con ese nombre de usuario!",
            "Usuarios",JOptionPane.WARNING_MESSAGE);
        else if(this.TxtUsername.getText().isEmpty())
        JOptionPane.showMessageDialog(this, "El Nombre de usuario esta vacio!",
            "Información necesaria",JOptionPane.WARNING_MESSAGE);
        else if(this.TxtFullname.getText().isEmpty())
        JOptionPane.showMessageDialog(this, "El Nombre completo esta vacio!",
            "Información necesaria",JOptionPane.WARNING_MESSAGE);
        else if(this.TxtPassword.getText().isEmpty())
        JOptionPane.showMessageDialog(this, "La contraseña esta vacia!",
            "Información necesaria",JOptionPane.WARNING_MESSAGE);
        else if(!(this.TxtRPassword.getText().equals(this.TxtPassword.getText())))
        JOptionPane.showMessageDialog(this, "Contraseñas diferentes!",
            "Información necesaria",JOptionPane.WARNING_MESSAGE);
        else{
            this.Info.add(new Users(this.TxtUsername.getText(),this.TxtFullname.getText(),this.TxtPassword.getText()));
            JOptionPane.showMessageDialog(this,"Información guardada con éxito.","Registro",
                JOptionPane.INFORMATION_MESSAGE);
            this.setVisible(false);
        }
    }//GEN-LAST:event_BtnRegistrarMouseClicked

