    public boolean validarInfo(String name,String pass){
        for(int i=0;i<Info.size();i++){
            if(name.equals(Info.get(i).getUsername())&&
                    pass.equals(Info.get(i).getPassword())){
                return true;
            }
        }
        return false;
    }
    
    private boolean buscarInfo(String username){
        for(int i=0;i<Info.size();i++){
            if(Info.get(i).getUsername().equals(username))
                return true;
        }
        return false;
    }
    
